import { useEffect, useState } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { supabase } from '../lib/supabase';

export default function AdminRoute() {
    const [isAdmin, setIsAdmin] = useState<boolean | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function checkAdmin() {
            try {
                const { data: { session } } = await supabase.auth.getSession();

                if (!session) {
                    setIsAdmin(false);
                    setLoading(false);
                    return;
                }

                // Query the profiles table for the role
                const { data: profile, error } = await supabase
                    .from('profiles')
                    .select('role')
                    .eq('id', session.user.id)
                    .single();

                if (error || !profile) {
                    setIsAdmin(false);
                } else {
                    setIsAdmin(profile.role === 'admin');
                }
            } catch (err) {
                setIsAdmin(false);
            } finally {
                setLoading(false);
            }
        }

        checkAdmin();
    }, []);

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-bg-deep">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-purple"></div>
            </div>
        );
    }

    if (!isAdmin) {
        // If user is logged in but not admin, maybe send to dashboard? Handled by redirect.
        return <Navigate to="/dashboard" replace />;
    }

    return <Outlet />;
}

import { useState, useRef, useEffect } from 'react';
import { Search, Bell, User, LogOut, Settings as SettingsIcon } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { supabase } from '../lib/supabase';

interface DashboardHeaderProps {
    title: string;
    className?: string;
}

export default function DashboardHeader({ title, className = 'mb-8' }: DashboardHeaderProps) {
    const [isProfileOpen, setIsProfileOpen] = useState(false);
    const [isNotifOpen, setIsNotifOpen] = useState(false);
    const [fullName, setFullName] = useState<string>('');
    const [avatarUrl, setAvatarUrl] = useState<string | null>(null);
    const profileRef = useRef<HTMLDivElement>(null);
    const notifRef = useRef<HTMLDivElement>(null);
    const navigate = useNavigate();
    const { signOut, user } = useAuth();

    // Close dropdowns on outside click
    useEffect(() => {
        function handleClickOutside(event: MouseEvent) {
            if (profileRef.current && !profileRef.current.contains(event.target as Node)) {
                setIsProfileOpen(false);
            }
            if (notifRef.current && !notifRef.current.contains(event.target as Node)) {
                setIsNotifOpen(false);
            }
        }
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, []);

    // Fetch user profile data
    useEffect(() => {
        if (user) {
            const fetchProfile = async () => {
                const { data } = await supabase
                    .from('profiles')
                    .select('full_name, avatar_url')
                    .eq('id', user.id)
                    .single();

                if (data) {
                    setFullName(data.full_name || '');
                    setAvatarUrl(data.avatar_url || null);
                }
            };
            fetchProfile();

            // Realtime subscription to profile changes
            const channel = supabase.channel('custom-all-channel')
                .on(
                    'postgres_changes',
                    { event: 'UPDATE', schema: 'public', table: 'profiles', filter: `id=eq.${user.id}` },
                    (payload) => {
                        const newProfile = payload.new as any;
                        setFullName(newProfile.full_name || '');
                        setAvatarUrl(newProfile.avatar_url || null);
                    }
                )
                .subscribe();

            return () => {
                supabase.removeChannel(channel);
            };
        }
    }, [user]);

    const handleLogout = async () => {
        await signOut();
        navigate('/login');
    };

    const getInitials = (nameStr: string) => {
        return nameStr ? nameStr.split(' ').map(n => n[0]).join('').substring(0, 2).toUpperCase() : 'US';
    };

    const displayName = fullName || user?.email?.split('@')[0] || 'User';

    return (
        <header className={`flex items-center justify-between py-2 md:py-4 ${className}`}>
            <div className="flex items-center gap-4 md:gap-6">
                <h1 className="text-xl md:text-3xl font-bold truncate">{title}</h1>
                <div className="relative hidden lg:block">
                    <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-text-secondary" />
                    <input
                        type="text"
                        placeholder="Search"
                        className="bg-[rgba(40,36,84,0.4)] rounded-full py-2 pl-12 pr-6 text-sm outline-none border border-white/5 focus:border-primary-purple/50 focus:ring-1 focus:ring-primary-purple transition-all w-64 text-white"
                    />
                </div>
            </div>

            <div className="flex items-center gap-6">
                {/* Notifications */}
                <div className="relative" ref={notifRef}>
                    <button
                        onClick={() => { setIsNotifOpen(!isNotifOpen); setIsProfileOpen(false); }}
                        className="relative p-2 rounded-full hover:bg-white/5 transition-colors"
                    >
                        <Bell className="w-6 h-6 text-text-secondary hover:text-white transition-colors" />
                        <span className="absolute top-1 right-1 w-2.5 h-2.5 bg-primary-purple rounded-full ring-2 ring-bg-deep" />
                    </button>

                    {isNotifOpen && (
                        <div className="absolute right-0 mt-3 w-80 bg-[#131128] rounded-2xl border border-white/10 shadow-[0_8px_32px_rgba(0,0,0,0.5)] py-2 z-50">
                            <div className="px-4 py-3 border-b border-white/5 flex justify-between items-center">
                                <h3 className="font-bold text-white leading-none">Notifica├º├Áes</h3>
                                <span className="text-xs bg-primary-purple/20 text-primary-purple px-2 py-0.5 rounded-full font-bold">2 Novas</span>
                            </div>
                            <div className="max-h-80 overflow-y-auto">
                                <div className="px-4 py-3 hover:bg-white/5 cursor-pointer border-b border-white/5 last:border-0 transition-colors relative">
                                    <div className="absolute left-0 top-0 bottom-0 w-1 bg-primary-purple rounded-r-full" />
                                    <p className="text-sm text-white mb-1"><span className="text-green-500 font-semibold">Deposit Successful</span></p>
                                    <p className="text-xs text-text-secondary leading-relaxed">Your deposit of 0.5 BTC has been processed and credited to your wallet.</p>
                                    <p className="text-[10px] uppercase tracking-wider text-text-secondary mt-2">10 min ago</p>
                                </div>
                                <div className="px-4 py-3 hover:bg-white/5 cursor-pointer border-b border-white/5 last:border-0 transition-colors relative">
                                    <div className="absolute left-0 top-0 bottom-0 w-1 bg-primary-purple rounded-r-full" />
                                    <p className="text-sm text-white mb-1"><span className="text-red-400 font-semibold">Security Alert</span></p>
                                    <p className="text-xs text-text-secondary leading-relaxed">New login from Chrome on Windows device detected.</p>
                                    <p className="text-[10px] uppercase tracking-wider text-text-secondary mt-2">2 hours ago</p>
                                </div>
                            </div>
                            <div className="px-4 pt-2 pb-1 border-t border-white/5 text-center">
                                <button className="text-sm text-primary-purple hover:text-white transition-colors font-medium">Ver todas as notifica├º├Áes</button>
                            </div>
                        </div>
                    )}
                </div>

                {/* Profile */}
                <div className="relative" ref={profileRef}>
                    <div
                        onClick={() => { setIsProfileOpen(!isProfileOpen); setIsNotifOpen(false); }}
                        className="flex items-center gap-2 md:gap-3 bg-[rgba(40,36,84,0.4)] py-1.5 px-2 rounded-full cursor-pointer hover:bg-[rgba(40,36,84,0.6)] transition-all border border-white/5 hover:border-primary-purple/30 group"
                    >
                        <span className="hidden sm:block text-sm font-medium pl-3 pr-1 text-text-secondary group-hover:text-white transition-colors capitalize truncate max-w-[120px]">{displayName}</span>
                        {avatarUrl ? (
                            <img
                                src={avatarUrl}
                                alt="Avatar"
                                className="w-8 h-8 rounded-full object-cover shadow-[0_0_10px_rgba(102,57,228,0.5)] shrink-0"
                            />
                        ) : (
                            <div className="w-8 h-8 rounded-full bg-primary-purple flex items-center justify-center font-bold text-sm text-white shadow-[0_0_10px_rgba(102,57,228,0.5)] shrink-0">
                                {getInitials(displayName)}
                            </div>
                        )}
                    </div>

                    {isProfileOpen && (
                        <div className="absolute right-0 mt-3 w-56 bg-[#131128] rounded-2xl border border-white/10 shadow-[0_8px_32px_rgba(0,0,0,0.5)] py-2 z-50 overflow-hidden">
                            <div className="px-4 py-3 border-b border-white/5 mb-1 bg-white/[0.02]">
                                <p className="text-sm font-bold text-white capitalize truncate">{displayName}</p>
                                <p className="text-xs text-text-secondary truncate">{user?.email}</p>
                            </div>

                            <button
                                onClick={() => { navigate('/settings'); setIsProfileOpen(false); }}
                                className="w-full text-left px-4 py-2.5 flex items-center gap-3 text-sm text-text-secondary hover:text-white hover:bg-white/5 transition-colors"
                            >
                                <User size={16} /> Meu Perfil
                            </button>
                            <button
                                onClick={() => { navigate('/settings'); setIsProfileOpen(false); }}
                                className="w-full text-left px-4 py-2.5 flex items-center gap-3 text-sm text-text-secondary hover:text-white hover:bg-white/5 transition-colors"
                            >
                                <SettingsIcon size={16} /> Configura├º├Áes
                            </button>

                            <div className="h-px bg-white/5 my-1" />

                            <button
                                onClick={handleLogout}
                                className="w-full text-left px-4 py-2.5 flex items-center gap-3 text-sm text-red-400 hover:text-red-300 hover:bg-red-500/10 transition-colors"
                            >
                                <LogOut size={16} /> Sair da aplica├º├úo
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </header>
    );
}

import { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

export default function UnlimitWidget() {
    const { user } = useAuth();
    const [action, setAction] = useState<'buy' | 'sell'>('buy');
    const [fiatCurrency, setFiatCurrency] = useState('USD');
    const [cryptoCurrency, setCryptoCurrency] = useState('BTC');
    const [fiatAmount, setFiatAmount] = useState('500');
    const [cryptoAmount, setCryptoAmount] = useState('0.00735');
    const [isLoading, setIsLoading] = useState(false);

    // Mock API call to get quote from our custom backend (which will talk to Unlimit)
    const getQuote = () => {
        setIsLoading(true);
        setTimeout(() => {
            if (action === 'buy') {
                const amount = parseFloat(fiatAmount) || 0;
                setCryptoAmount((amount / 68000).toFixed(5));
            } else {
                const amount = parseFloat(cryptoAmount) || 0;
                setFiatAmount((amount * 68000).toFixed(2));
            }
            setIsLoading(false);
        }, 1200);
    };

    useEffect(() => {
        const timeoutId = setTimeout(() => {
            getQuote();
        }, 800);
        return () => clearTimeout(timeoutId);
    }, [fiatAmount, cryptoAmount, action, fiatCurrency, cryptoCurrency]);

    const handleTransaction = async () => {
        if (!user) {
            // Should prompt login
            alert("Please login to process transaction");
            return;
        }

        // This is where we will call the Supabase Edge Function to generate the Unlimit payment link securely
        alert(`Proceeding securely to buy ${cryptoAmount} ${cryptoCurrency} for ${fiatAmount} ${fiatCurrency}. (Backend Link Generation Pending)`);
    };

    return (
        <div className="bg-bg-surface rounded-[40px] p-6 border border-white/5 shadow-2xl relative overflow-hidden group min-h-[500px] flex flex-col">
            {/* Decorative Glow */}
            <div className="absolute top-0 right-0 w-32 h-32 bg-primary-purple/20 blur-3xl opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none" />

            <div className="flex justify-between items-center mb-6 relative z-10">
                <h3 className="text-2xl font-bold">Fiat Gateway</h3>
                <div className="flex bg-[#100e23] rounded-full p-1 border border-white/5">
                    <button
                        className={`px-4 py-1.5 rounded-full text-sm font-semibold transition-colors ${action === 'buy' ? 'bg-primary-purple text-white' : 'text-text-secondary hover:text-white'}`}
                        onClick={() => setAction('buy')}
                    >
                        Buy
                    </button>
                    <button
                        className={`px-4 py-1.5 rounded-full text-sm font-semibold transition-colors ${action === 'sell' ? 'bg-primary-purple text-white' : 'text-text-secondary hover:text-white'}`}
                        onClick={() => setAction('sell')}
                    >
                        Sell
                    </button>
                </div>
            </div>

            <div className="flex-1 w-full flex flex-col relative z-10 mt-2 space-y-6">
                {/* You Pay Section */}
                <div>
                    <label className="text-xs text-text-secondary font-medium mb-2 block uppercase tracking-wider">
                        {action === 'buy' ? 'You Pay' : 'You Sell'}
                    </label>
                    <div className="flex bg-[#131128] rounded-2xl p-4 border border-white/5 items-center transition-all focus-within:border-primary-purple focus-within:shadow-[0_0_15px_rgba(102,57,228,0.2)]">
                        <input
                            type="text"
                            className="bg-transparent border-none outline-none flex-1 text-2xl font-bold w-full"
                            value={action === 'buy' ? fiatAmount : cryptoAmount}
                            onChange={(e) => action === 'buy' ? setFiatAmount(e.target.value) : setCryptoAmount(e.target.value)}
                        />
                        <select
                            className="bg-[#282454] border border-white/10 outline-none text-white font-bold py-2 px-4 rounded-xl ml-4 cursor-pointer hover:bg-[#342e6d] transition-colors"
                            value={action === 'buy' ? fiatCurrency : cryptoCurrency}
                            onChange={(e) => action === 'buy' ? setFiatCurrency(e.target.value) : setCryptoCurrency(e.target.value)}
                        >
                            {action === 'buy' ? (
                                <>
                                    <option value="USD">USD</option>
                                    <option value="EUR">EUR</option>
                                    <option value="BRL">BRL</option>
                                </>
                            ) : (
                                <>
                                    <option value="BTC">BTC</option>
                                    <option value="ETH">ETH</option>
                                    <option value="USDT">USDT</option>
                                </>
                            )}
                        </select>
                    </div>
                </div>

                {/* Swap Icon */}
                <div className="flex justify-center -my-3 relative z-20">
                    <div className="bg-[#282454] p-2 rounded-full border border-white/5 shadow-lg">
                        <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-text-secondary">
                            <line x1="12" y1="5" x2="12" y2="19"></line>
                            <polyline points="19 12 12 19 5 12"></polyline>
                        </svg>
                    </div>
                </div>

                {/* You Receive Section */}
                <div>
                    <label className="text-xs text-text-secondary font-medium mb-2 block uppercase tracking-wider">
                        You Receive
                    </label>
                    <div className="flex bg-[#131128] rounded-2xl p-4 border border-white/5 items-center">
                        <div className="flex-1 flex flex-col">
                            {isLoading ? (
                                <div className="h-8 w-32 bg-white/10 rounded animate-pulse" />
                            ) : (
                                <span className="text-2xl font-bold text-primary-purple">
                                    {action === 'buy' ? cryptoAmount : fiatAmount}
                                </span>
                            )}
                        </div>
                        <select
                            className="bg-[#282454] border border-white/10 outline-none text-white font-bold py-2 px-4 rounded-xl ml-4 cursor-pointer hover:bg-[#342e6d] transition-colors"
                            value={action === 'buy' ? cryptoCurrency : fiatCurrency}
                            onChange={(e) => action === 'buy' ? setCryptoCurrency(e.target.value) : setFiatCurrency(e.target.value)}
                        >
                            {action === 'buy' ? (
                                <>
                                    <option value="BTC">BTC</option>
                                    <option value="ETH">ETH</option>
                                    <option value="USDT">USDT</option>
                                </>
                            ) : (
                                <>
                                    <option value="USD">USD</option>
                                    <option value="EUR">EUR</option>
                                    <option value="BRL">BRL</option>
                                </>
                            )}
                        </select>
                    </div>
                    {isLoading ? (
                        <p className="text-xs text-text-secondary mt-2 text-right animate-pulse">Calculating rate...</p>
                    ) : (
                        <p className="text-xs text-text-secondary mt-2 text-right">
                            1 {action === 'buy' ? cryptoCurrency : fiatCurrency} Ôëê {action === 'buy' ? (68000).toLocaleString() : (1 / 68000).toFixed(8)} {action === 'buy' ? fiatCurrency : cryptoCurrency}
                        </p>
                    )}
                </div>

                <div className="flex-1 flex flex-col justify-end mt-4">
                    <button
                        onClick={handleTransaction}
                        disabled={isLoading}
                        className={`w-full bg-primary-purple hover:bg-[#7b46ff] text-white font-bold py-4 rounded-xl transition-all shadow-[0_4px_14px_0_rgba(102,57,228,0.39)] disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2`}
                    >
                        {isLoading && (
                            <div className="w-5 h-5 border-2 border-white/20 border-t-white rounded-full animate-spin" />
                        )}
                        {action === 'buy' ? `Buy ${cryptoCurrency}` : `Sell ${cryptoCurrency}`}
                    </button>

                    <p className="text-xs text-center text-text-secondary mt-4 flex items-center justify-center gap-2">
                        <span className="w-2 h-2 rounded-full bg-green-500 shadow-[0_0_8px_#22c55e]"></span>
                        Secure Server-to-Server White-label API
                    </p>
                </div>
            </div>
        </div>
    );
}

import { createContext, useContext, useEffect, useState } from 'react';
import type { Session, User } from '@supabase/supabase-js';
import { supabase } from '../lib/supabase';

interface AuthContextType {
    session: Session | null;
    user: User | null;
    loading: boolean;
    signOut: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType>({
    session: null,
    user: null,
    loading: true,
    signOut: async () => { },
});

export const useAuth = () => {
    return useContext(AuthContext);
};

export function AuthProvider({ children }: { children: React.ReactNode }) {
    const [session, setSession] = useState<Session | null>(null);
    const [user, setUser] = useState<User | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Obter sess├úo inicial
        supabase.auth.getSession().then(({ data: { session } }) => {
            setSession(session);
            setUser(session?.user ?? null);
            setLoading(false);
        });

        // Escutar mudan├ºas de estado de autentica├º├úo (login, logout, token refresh)
        const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
            setSession(session);
            setUser(session?.user ?? null);
            setLoading(false);
        });

        return () => subscription.unsubscribe();
    }, []);

    const signOut = async () => {
        setLoading(true);
        await supabase.auth.signOut();
        setLoading(false);
    };

    const value = {
        session,
        user,
        loading,
        signOut,
    };

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
}

import { useState, useEffect } from 'react';
import UnlimitWidget from '../components/UnlimitWidget';
import DashboardHeader from '../components/DashboardHeader';
import TokenCard from '../components/TokenCard';

export default function BuyDashboard() {
    const [tokens, setTokens] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTopTokens = async () => {
            setLoading(true);
            try {
                // Fetch top 6 coins by market cap
                const response = await fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=6&page=1&sparkline=true&price_change_percentage=24h`);
                const data = await response.json();
                if (data && data.length > 0) {
                    setTokens(data);
                }
            } catch (error) {
                console.error("Failed to fetch top tokens", error);
            } finally {
                setLoading(false);
            }
        };

        fetchTopTokens();
    }, []);

    return (
        <div className="pt-4 md:pt-8 px-4 md:px-8 xl:px-12 w-full max-w-[1800px] mx-auto min-h-screen pb-24 md:pb-12 pt-safe">
            <DashboardHeader title="Buy & Sell" className="mb-6 md:mb-12" />

            <div className="flex flex-col xl:flex-row gap-6 md:gap-8 mt-4 md:mt-8">
                {/* Left Column: Fiat Gateway Widget */}
                <div className="w-full xl:w-[450px] shrink-0">
                    <h2 className="text-3xl font-bold mb-4">Trade Crypto easily</h2>
                    <p className="text-text-secondary text-lg mb-8 leading-relaxed">
                        Securely buy, sell, and exchange bridging the gap between traditional banking and crypto via our secure fiat gateway.
                    </p>
                    <UnlimitWidget />
                </div>

                {/* Right Column: Grid of Cryptos */}
                <div className="flex-1">
                    <h3 className="text-2xl font-bold mb-6">Market Overview</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 xl:grid-cols-2 2xl:grid-cols-3 3xl:grid-cols-4 gap-6">
                        {loading ? (
                            Array.from({ length: 6 }).map((_, i) => (
                                <TokenCard
                                    key={i}
                                    isLoading={true}
                                    name="" symbol="" price={0} change24h={0} sparklineData={[]}
                                />
                            ))
                        ) : (
                            tokens.map(token => (
                                <TokenCard
                                    key={token.id}
                                    name={token.name}
                                    symbol={token.symbol.toUpperCase()}
                                    price={token.current_price}
                                    change24h={token.price_change_percentage_24h}
                                    walletAddress="Public Market"
                                    sparklineData={token.sparkline_in_7d?.price || []}
                                    iconUrl={token.image}
                                    hideBuyButton={true}
                                />
                            ))
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

import { useState, useEffect } from 'react';
