    { id: 'ethereum', address: '1M5TdPV8G6YfcKkrNEyhPu3nBpWtGXJGho' },
    { id: 'ripple', address: '1AGm6Jc43FUaYRkm8wj2cBpJTFWqjxwCXW' },
    { id: 'litecoin', address: '1P9RQEr2xeE3PEb44ZE35sfZRRW1JHU8qx' },
