export default function Dashboard() {
    const [tokensList, setTokensList] = useState(() => {
        const saved = localStorage.getItem('aion_tokens');
        if (saved) {
            try {
                return JSON.parse(saved);
            } catch (e) {
                return defaultTokensList;
            }
        }
        return defaultTokensList;
    });

